import { Col,Row, } from "react-bootstrap";
import React, {useState, useEffect} from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from "axios";




const UserCard = ()=>{
    const url = "http://127.0.0.1:8000/users";
    const [users, setUsers] = useState([])
    const [show, setShow] = useState([])


    function getUsers() {

    axios.get(url)
        .then(response=>{
            setUsers(response.data)
        })}

    function ApplyUsers () {
         setShow([])
            users.map(user=> {

                let des = <p>{user.description.substr(0,130)}<strong style={{fontSize:'20px'}}>...</strong></p>
                const new_image = (

                    <Col md={4}>
                        <div key={user.id} style={{marginBottom:'20px'}}>
                        <Card className='text-center'>
                          <Card.Img style={{width: '100%', maxHeight:'210px'}} src={user.image}  />
                          <Card.Body>
                            <Card.Title>{`${user.name} ${user.surname}`}</Card.Title>
                            <Card.Text>{des}</Card.Text>
                            {/*<Link to={`details/${arches.id}`}><Button variant="primary">Learn more</Button></Link>*/}
                          </Card.Body>
                           {/* <Card.Footer className='text-muted' ><img src={iconLocation}*/}
                           {/*style={{width:'20px', marginBottom:'4px'}}/> {arches.place}</Card.Footer>*/}
                        </Card>
                        </div>
                    </Col>
                 );

                 setShow(arry=>[...arry,new_image])});
        }
        return (
            <div>
                <Container>
                    <div className='rows' >
                        <Row>
                            {show.length>0 ? show : 'Nothing tho show'}
                        </Row>
                    </div>
                </Container>
            </div>

      )

}




export  default UserCard
