import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, {useState} from 'react';
import axios from "axios";




const loginForm = ()=>{
  const url = "http://127.0.0.1:8000/users";
  const userInfo = {
    email: '',
    password: ''
  }

  function handleCheckUser(event){
    const target = event.target
    userInfo.email = target.type==='email' ? target.value : userInfo.email
    userInfo.email = target.value


  }

  const handlePrevent = (event)=> {
    event.preventDefault()

  }


  return (
      <>
          <h1 style={{textAlign:'center'}}>Welcome To ChatApp</h1>
          <Form style={{marginLeft:'25%', marginRight:'25%',marginTop:'10%'}}>

            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control  onChange={handleCheckUser} type="email" placeholder="Enter email" />
              <Form.Text className="text-muted" >
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control className='password' type="password" placeholder="Password" />
            </Form.Group>

            <Button  type="submit" >
              Login
            </Button>
            <Button onSubmit={handleCheckUser} variant="primary" type="submit" style={{marginLeft:'5px'}}>
              Create An Account
            </Button>
          </Form>
          </>
  );
}

export default loginForm;
