import Card from 'react-bootstrap/Card';
import CardGroup from 'react-bootstrap/CardGroup';
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState, useEffect,} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import Button from 'react-bootstrap/Button';
import { Row, Col, Grid, Container} from "react-bootstrap";


const HomePage = ()=>{
  const url = 'http://127.0.0.1:8000/users'
  const [users, setUsers] = useState({})
  const [userShow, setUserShw] = useState({})

  const getUsers = ()=> {
      fetch(url)
          .then(res => res.json())
          .then(json=>json.forEach((user)=>{console.log(user)}))
  }

  function applyImages ()  {
        setUserShw([])


        let des = <p>{user.description.substr(0,130)}<strong style={{fontSize:'20px'}}>...</strong></p>
            const new_image = (

                <Col md={4}>
                    <div key={user.id} style={{marginBottom:'20px'}}>
                    <Card className='text-center'>
                      <Card.Img style={{width: '100%', maxHeight:'210px'}} src={user.image}  />
                      <Card.Body>
                        <Card.Title>{user.title}</Card.Title>
                        <Card.Text>{des}</Card.Text>
                        <Link to={`details/${user.id}`}><Button variant="primary">Learn more</Button></Link>
                      </Card.Body>
                        <Card.Footer className='text-muted' > {user.email}</Card.Footer>
                    </Card>
                    </div>
                </Col>
             );

             setUserShw(arry=>[...arry,new_image])}}




   useEffect(()=>{
     getUsers()
       },[1])

  useEffect(()=>{
    applyImages()
  }, [users])



export default HomePage;
