import { Row, Col} from "react-bootstrap";
import Container from 'react-bootstrap/Container';
import React, {useState, useEffect} from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from "axios";
import {Link} from "react-router-dom";




const UserCard = ({setUserName})=>{
    const url = "http://127.0.0.1:8000/users";
    const [users, setUsers] = useState([])
    const [show, setShow] = useState([])


    function getUsers() {

    axios.get(url)
        .then(response=>{
            setUsers(response.data)
        })}

    function applyUsers () {
         setShow([])
            users.map(user=> {

                setUserName(`${user.name} ${user.surname}`)

                const new_image = (

                    <Col md={4}>
                        <div key={user.id} style={{marginBottom:'20px'}}>
                        <Card className='text-center'>
                          <Card.Img style={{width: '150',paddingLeft:'0px', paddingRight:'0px', height:'250px'}} src={user.image}  />
                          <Card.Body>
                            <Card.Title>{`${user.name} ${user.surname}`}</Card.Title>
                            <Card.Text>{`Hobbies: ${user.description}`}</Card.Text>
                            <Link to={`user/${user.id}`}><Button variant="primary">Edit User</Button></Link>
                          </Card.Body>
                            <Card.Footer className='text-muted' >{`Email: ${user.email}`}</Card.Footer>
                        </Card>
                        </div>
                    </Col>
                 );

                 setShow(arry=>[...arry,new_image])});

        }

     useEffect(()=>{
    getUsers();
      }, [])

    useEffect(()=>{
        applyUsers()
    },[users])

    return (
        <div style={{marginTop:'20px'}}>
            <Container>
                <div className='rows' >
                    <Row>
                        {show.length>0 ? show : 'Nothing tho show'}
                    </Row>
                </div>
            </Container>
        </div>

  )

}




export  default UserCard
