import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, {useState} from 'react';
import axios from "axios";
import {Link} from "react-router-dom";




const loginForm = ()=>{
  const url = "http://127.0.0.1:8000/users";
  const [changePage, setChangePage] = useState(false)
  const userInfo = {
    email: '',
    password: ''
  }

  function handleCheckUser(event){
    const target = event.target
    userInfo.email = target.type==='email' ? target.value : userInfo.email
    userInfo.password = target.type==='password' ? target.value : userInfo.password
    console.log(userInfo)


  }

  const handlePrevent = (event)=> {
    event.preventDefault()
    axios.get(url)
        .then(response=>{
            response.data.map((user)=>{
              if (user.email===userInfo.email && user.password===userInfo.password){
                setChangePage(true)
              }
            })
        })


  }


  return (
      <>
          <h1 style={{textAlign:'center'}}>Welcome To ChatApp</h1>
          <Form style={{marginLeft:'25%', marginRight:'25%',marginTop:'10%'}}>

            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control  onChange={handleCheckUser} type="email" placeholder="Enter email" />
              <Form.Text className="text-muted" >
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control onChange={handleCheckUser} type="password" placeholder="Password" />
            </Form.Group>

           <Link to={`${changePage  ? 'home' : 'login'}`}> <Button  type="submit" >
              Login
            </Button></Link>
            <Button onSubmit={handlePrevent} variant="primary" type="submit" style={{marginLeft:'5px'}}>
              Create An Account
            </Button>
          </Form>
          </>
  );
}

export default loginForm;
